import { useQuery } from "@tanstack/react-query";
import { Card, CardContent } from "@/components/ui/card";
import { Wallet, TrendingUp, TrendingDown, Receipt } from "lucide-react";
import { MONTHS } from "@/lib/constants";
import { formatCurrency } from "@/lib/utils";

interface BudgetCardsProps {
  month: number;
  year: number;
}

export default function BudgetCards({ month, year }: BudgetCardsProps) {
  const { data: budgetSummary, isLoading } = useQuery({
    queryKey: ["/api/analytics/budget-summary", month, year],
    queryFn: async () => {
      const response = await fetch(`/api/analytics/budget-summary/${month}/${year}`, {
        credentials: "include",
      });

      if (!response.ok) {
        throw new Error(`${response.status}: ${response.statusText}`);
      }

      return response.json();
    },
  });

  if (isLoading) {
    return (
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        {[...Array(4)].map((_, i) => (
          <Card key={i} className="animate-pulse">
            <CardContent className="p-6">
              <div className="flex items-center justify-between mb-4">
                <div className="w-12 h-12 bg-muted rounded-lg" />
                <div className="w-20 h-5 bg-muted rounded-full" />
              </div>
              <div className="space-y-2">
                <div className="w-24 h-4 bg-muted rounded" />
                <div className="w-32 h-8 bg-muted rounded" />
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    );
  }

  if (!budgetSummary) {
    return (
      <div className="text-center py-8">
        <div className="text-muted-foreground">Failed to load expense summary</div>
      </div>
    );
  }

  const cards = [
    {
      title: "Total Wallet Balance",
      value: formatCurrency(budgetSummary.walletAmount),
      icon: Wallet,
      color: "bg-primary/10 text-primary",
      badge: "TOTAL RECHARGED",
    },
    {
      title: "Monthly Expenses",
      value: formatCurrency(budgetSummary.totalExpenses),
      icon: TrendingUp,
      color: "bg-destructive/10 text-destructive",
      badge: `${MONTHS[month - 1].substring(0, 3).toUpperCase()} ${year}`,
    },
    {
      title: "Available Balance",
      value: formatCurrency(budgetSummary.remainingAmount),
      icon: TrendingDown,
      color: budgetSummary.remainingAmount < 0 ? "bg-destructive/10 text-destructive" : "bg-green-100 text-green-600",
      badge: "REMAINING",
    },
    {
      title: "Monthly Transactions",
      value: budgetSummary.expenseCount.toString(),
      icon: Receipt,
      color: "bg-accent text-muted-foreground",
      badge: `${MONTHS[month - 1].substring(0, 3).toUpperCase()}`,
    },
  ];

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
      {cards.map((card, index) => {
        const Icon = card.icon;
        return (
          <Card key={index}>
            <CardContent className="p-6">
              <div className="flex items-center justify-between mb-4">
                <div className={`w-12 h-12 ${card.color} rounded-lg flex items-center justify-center`}>
                  <Icon className="w-6 h-6" />
                </div>
                <span className="text-xs font-medium text-muted-foreground bg-accent px-2 py-1 rounded-full">
                  {card.badge}
                </span>
              </div>
              <div>
                <h3 className="text-sm font-medium text-muted-foreground mb-1">{card.title}</h3>
                <p className="text-3xl font-bold text-foreground" data-testid={`text-${card.title.toLowerCase().replace(/\s+/g, '-')}`}>
                  {card.value}
                </p>
              </div>
            </CardContent>
          </Card>
        );
      })}
    </div>
  );
}

client/src/components/ExpenseCharts.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, PieChart, Pie, Cell, Legend } from "recharts";
import { useState } from "react";
import { formatCurrency } from "@/lib/utils";

interface ExpenseChartsProps {
  month?: number;
  year?: number;
}

export default function ExpenseCharts({ month, year }: ExpenseChartsProps) {
  const [trendMonths, setTrendMonths] = useState("6");

  // Fetch monthly expense trends
  const { data: trendsData, isLoading: trendsLoading } = useQuery({
    queryKey: ["/api/analytics/expense-trends-monthly", trendMonths],
    queryFn: async () => {
      const response = await fetch(`/api/analytics/expense-trends-monthly/${trendMonths}`, {
        credentials: "include",
      });

      if (!response.ok) {
        throw new Error(`${response.status}: ${response.statusText}`);
      }

      return response.json();
    },
  });

  // Fetch category breakdown
  const { data: categoryData, isLoading: categoryLoading } = useQuery({
    queryKey: ["/api/analytics/category-breakdown", month, year],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (month) params.append('month', month.toString());
      if (year) params.append('year', year.toString());

      const response = await fetch(`/api/analytics/category-breakdown?${params.toString()}`, {
        credentials: "include",
      });

      if (!response.ok) {
        throw new Error(`${response.status}: ${response.statusText}`);
      }

      return response.json();
    },
  });

  const formatTrendData = (data: any[]) => {
    return data.map(item => ({
      ...item,
      amount: Number(item.amount) || 0,
      month: item.month ? new Date(item.month + '-01').toLocaleDateString('en-US', { 
        month: 'short',
        year: 'numeric' 
      }) : item.date,
    }));
  };

  const getMonthsLabel = (months: string) => {
    switch (months) {
      case "3": return "Last 3 months";
      case "6": return "Last 6 months";
      case "12": return "Last 12 months";
      default: return "Last 6 months";
    }
  };

  // Colors for pie chart - distinct colors like the reference image
  const COLORS = [
    "#3B82F6", // Blue
    "#8B5CF6", // Purple  
    "#A855F7", // Violet
    "#EC4899", // Pink
    "#EF4444", // Red
    "#F97316", // Orange
    "#EAB308", // Yellow
    "#84CC16", // Lime
    "#22C55E", // Green
    "#10B981", // Emerald
    "#06B6D4", // Cyan
    "#0EA5E9", // Sky blue
    "#6366F1", // Indigo
    "#8B5A2B", // Brown
    "#6B7280", // Gray
  ];

  const formatPieData = (data: any[]) => {
    const total = data.reduce((sum, item) => sum + parseFloat(item.totalAmount), 0);
    return data.map((item, index) => {
      const value = parseFloat(item.totalAmount);
      return {
        ...item,
        value,
        percentage: (value / total) * 100,
        fill: COLORS[index % COLORS.length],
      };
    });
  };

  // Removed custom label function - no longer showing percentages on pie slices

  // Calculate total amount from trends data
  const totalAmount = trendsData?.reduce((sum: number, item: any) => sum + (parseFloat(item.amount) || 0), 0) || 0;

  return (
    <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
      {/* Expense Trends Chart */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle>Monthly Expense Trends</CardTitle>
            <Select value={trendMonths} onValueChange={setTrendMonths}>
              <SelectTrigger className="w-40" data-testid="select-trend-period">
                <SelectValue />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="3">Last 3 months</SelectItem>
                <SelectItem value="6">Last 6 months</SelectItem>
                <SelectItem value="12">Last 12 months</SelectItem>
              </SelectContent>
            </Select>
          </div>
        </CardHeader>
        <CardContent>
          {/* Total Amount Display */}
          {trendsData && trendsData.length > 0 && (
            <div className="mb-6 text-center">
              <div className="text-3xl font-bold text-primary" data-testid="text-total-amount">
                {formatCurrency(totalAmount)}
              </div>
              <div className="text-sm text-muted-foreground mt-1">
                Total for {getMonthsLabel(trendMonths)}
              </div>
            </div>
          )}
          <div className="h-[300px]" data-testid="chart-expense-trends">
            {trendsLoading ? (
              <div className="flex items-center justify-center h-full">
                <div className="flex items-center space-x-2">
                  <div className="w-4 h-4 border-2 border-primary border-t-transparent rounded-full animate-spin" />
                  <span className="text-muted-foreground">Loading chart...</span>
                </div>
              </div>
            ) : trendsData && trendsData.length > 0 ? (
              <ResponsiveContainer width="100%" height="100%">
                <BarChart 
                  data={formatTrendData(trendsData)}
                  margin={{ top: 20, right: 30, left: 20, bottom: 5 }}
                >
                  <CartesianGrid 
                    strokeDasharray="2 2" 
                    stroke="hsl(var(--border))" 
                    vertical={false}
                  />
                  <XAxis 
                    dataKey="month" 
                    stroke="hsl(var(--muted-foreground))"
                    fontSize={11}
                    tickLine={false}
                    axisLine={false}
                  />
                  <YAxis 
                    stroke="hsl(var(--muted-foreground))"
                    fontSize={11}
                    tickLine={false}
                    axisLine={false}
                    tickFormatter={(value) => formatCurrency(value)}
                  />
                  <Tooltip 
                    contentStyle={{
                      backgroundColor: "rgba(0, 0, 0, 0.9)",
                      border: "1px solid rgba(255, 255, 255, 0.2)",
                      borderRadius: "8px",
                      color: "white",
                      boxShadow: "0 8px 16px rgba(0, 0, 0, 0.3)",
                      fontSize: "14px",
                      fontWeight: "500",
                    }}
                    formatter={(value: number) => [formatCurrency(value), "Amount"]}
                    cursor={{ fill: 'rgba(34, 197, 94, 0.1)' }}
                    labelStyle={{ color: "white", fontSize: "12px" }}
                  />
                  <Bar
                    dataKey="amount"
                    fill="#22C55E"
                    radius={[4, 4, 0, 0]}
                    maxBarSize={60}
                  />
                </BarChart>
              </ResponsiveContainer>
            ) : (
              <div className="flex items-center justify-center h-full">
                <div className="text-center text-muted-foreground">
                  <div className="text-4xl mb-2">ðŸ“Š</div>
                  <p>No data available</p>
                  <p className="text-xs">Add some expenses to see trends</p>
                </div>
              </div>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Category Breakdown */}
      <Card>
        <CardHeader className="pb-4">
          <CardTitle>Expense Breakdown by Category</CardTitle>
        </CardHeader>
        <CardContent className="p-8">
          <div className="h-[350px]" data-testid="category-breakdown">
            {categoryLoading ? (
              <div className="flex items-center justify-center h-full">
                <div className="flex items-center space-x-2">
                  <div className="w-4 h-4 border-2 border-primary border-t-transparent rounded-full animate-spin" />
                  <span className="text-muted-foreground">Loading chart...</span>
                </div>
              </div>
            ) : categoryData && categoryData.length > 0 ? (
              <ResponsiveContainer width="100%" height="100%">
                <PieChart>
                  <Pie
                    data={formatPieData(categoryData)}
                    cx="50%"
                    cy="45%"
                    outerRadius={110}
                    dataKey="value"
                    stroke="#fff"
                    strokeWidth={2}
                  >
                    {formatPieData(categoryData).map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={entry.fill} />
                    ))}
                  </Pie>
                  <Legend 
                    layout="horizontal"
                    verticalAlign="bottom"
                    align="center"
                    wrapperStyle={{
                      fontSize: '11px',
                      paddingTop: '20px',
                      lineHeight: '1.8'
                    }}
                    formatter={(value: string, entry: any) => (
                      <span style={{ color: "hsl(var(--foreground))", fontSize: '11px' }}>
                        {value} - {formatCurrency(entry.payload.value)}
                      </span>
                    )}
                  />
                </PieChart>
              </ResponsiveContainer>
            ) : (
              <div className="flex items-center justify-center h-full">
                <div className="text-center text-muted-foreground">
                  <div className="text-4xl mb-2">ðŸ“Š</div>
                  <p>No category data available</p>
                  <p className="text-xs">Add some expenses to see breakdown</p>
                </div>
              </div>
            )}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/ExpenseForm.tsx.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertExpenseSchema, type Category } from "@shared/schema";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { ObjectUploader } from "@/components/ObjectUploader";
import { useToast } from "@/hooks/use-toast";
import { Loader2, Upload, FileText, X } from "lucide-react";
import type { UploadResult } from "@uppy/core";

// Form input type (before schema transformation)
type ExpenseFormData = {
  description: string;
  amount: string;
  categoryId: string;
  vendor?: string | null;
  date: string; // Input as string, gets transformed to Date by schema
  receiptPath?: string | null;
  notes?: string | null;
};

interface ExpenseFormProps {
  onSuccess?: () => void;
  onCancel?: () => void;
  initialData?: Partial<ExpenseFormData>;
  expenseId?: string; // For edit mode
}

export default function ExpenseForm({ onSuccess, onCancel, initialData, expenseId }: ExpenseFormProps) {
  const isEditMode = !!expenseId;
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [uploadedReceiptUrl, setUploadedReceiptUrl] = useState<string | null>(initialData?.receiptPath || null);
  const [uploadedReceipts, setUploadedReceipts] = useState<string[]>(initialData?.receiptPath ? [initialData.receiptPath] : []);

  const form = useForm<ExpenseFormData>({
    resolver: zodResolver(insertExpenseSchema),
    defaultValues: {
      description: initialData?.description || "",
      amount: initialData?.amount || "",
      categoryId: initialData?.categoryId || "",
      vendor: initialData?.vendor || "",
      date: initialData?.date || new Date().toISOString().split('T')[0],
      notes: initialData?.notes || "",
      receiptPath: initialData?.receiptPath || null,
    },
  });

  // Fetch categories
  const { data: categories = [], isLoading: categoriesLoading } = useQuery<Category[]>({
    queryKey: ["/api/categories"],
  });

  // Create/Update expense mutation
  const createExpenseMutation = useMutation({
    mutationFn: async (data: ExpenseFormData) => {
      const method = isEditMode ? "PUT" : "POST";
      const url = isEditMode ? `/api/expenses/${expenseId}` : "/api/expenses";
      const response = await apiRequest(method, url, data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/expenses"] });
      queryClient.invalidateQueries({ queryKey: ["/api/analytics"] });
      toast({
        title: "Success",
        description: isEditMode ? "Expense updated successfully" : "Expense created successfully",
      });
      form.reset();
      setUploadedReceiptUrl(null);
      setUploadedReceipts([]);
      onSuccess?.();
    },
    onError: (error) => {
      toast({
        title: "Error",
        description: isEditMode ? "Failed to update expense" : "Failed to create expense",
        variant: "destructive",
      });
      console.error("Error creating expense:", error);
    },
  });

  const handleGetUploadParameters = async () => {
    const response = await apiRequest("POST", "/api/objects/upload", {});
    const data = await response.json();
    return {
      method: "PUT" as const,
      url: data.uploadURL,
    };
  };

  const removeReceipt = (receiptPath: string) => {
    setUploadedReceipts(prev => prev.filter(path => path !== receiptPath));
    if (receiptPath === form.getValues('receiptPath')) {
      form.setValue('receiptPath', null);
      setUploadedReceiptUrl(null);
    }
  };

  const handleUploadComplete = (result: UploadResult<Record<string, unknown>, Record<string, unknown>>) => {
    if (result.successful && result.successful.length > 0) {
      const uploadUrl = result.successful[0].uploadURL;
      if (uploadUrl) {
        setUploadedReceiptUrl(uploadUrl);

        // Extract the object path from the upload URL
        const url = new URL(uploadUrl);
        const pathParts = url.pathname.split('/');
        const bucketIndex = pathParts.findIndex(part => part === 'uploads');
        if (bucketIndex !== -1) {
          const objectId = pathParts.slice(bucketIndex + 1).join('/');
          const receiptPath = `/objects/uploads/${objectId}`;
          form.setValue('receiptPath', receiptPath);
          setUploadedReceipts(prev => [...prev, receiptPath]);
        }
      }

      toast({
        title: "Success",
        description: "Receipt uploaded successfully",
      });
    }
  };

  const onSubmit = (data: ExpenseFormData) => {
    createExpenseMutation.mutate(data);
  };

  return (
    <Card className="w-full max-w-2xl mx-auto">
      <CardHeader>
        <CardTitle>Add New Expense</CardTitle>
      </CardHeader>
      <CardContent>
        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <FormField
                control={form.control}
                name="date"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Date</FormLabel>
                    <FormControl>
                      <Input
                        type="date"
                        {...field}
                        value={field.value}
                        onChange={(e) => field.onChange(e.target.value)}
                        data-testid="input-date"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="amount"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Amount (â‚¹)</FormLabel>
                    <FormControl>
                      <Input
                        type="number"
                        step="0.01"
                        placeholder="0.00"
                        {...field}
                        data-testid="input-amount"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <FormField
              control={form.control}
              name="description"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Description</FormLabel>
                  <FormControl>
                    <Input
                      placeholder="Enter expense description"
                      {...field}
                      data-testid="input-description"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <FormField
                control={form.control}
                name="categoryId"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Category</FormLabel>
                    <Select onValueChange={field.onChange} value={field.value}>
                      <FormControl>
                        <SelectTrigger data-testid="select-category">
                          <SelectValue placeholder="Select a category" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {categories.map((category) => (
                          <SelectItem key={category.id} value={category.id}>
                            {category.name}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="vendor"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Vendor (Optional)</FormLabel>
                    <FormControl>
                      <Input
                        placeholder="Enter vendor name"
                        {...field}
                        value={field.value || ""}
                        data-testid="input-vendor"
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <div>
              <Label>Receipt Upload</Label>
              <div className="mt-2 space-y-3">
                {/* Show uploaded receipts */}
                {uploadedReceipts.length > 0 && (
                  <div className="space-y-2">
                    <p className="text-sm text-muted-foreground">
                      {uploadedReceipts.length} receipt{uploadedReceipts.length !== 1 ? 's' : ''} uploaded
                    </p>
                    {uploadedReceipts.map((receiptPath, index) => (
                      <div key={receiptPath} className="flex items-center justify-between p-3 bg-muted/50 rounded-lg border">
                        <div className="flex items-center space-x-2">
                          <FileText className="w-4 h-4 text-primary" />
                          <span className="text-sm font-medium">Receipt {index + 1}</span>
                        </div>
                        <Button
                          type="button"
                          variant="ghost"
                          size="sm"
                          onClick={() => removeReceipt(receiptPath)}
                          className="h-6 w-6 p-0 hover:bg-destructive/10 hover:text-destructive"
                        >
                          <X className="w-4 h-4" />
                        </Button>
                      </div>
                    ))}
                  </div>
                )}

                {/* Upload button */}
                <ObjectUploader
                  maxNumberOfFiles={1}
                  maxFileSize={10485760} // 10MB
                  onGetUploadParameters={handleGetUploadParameters}
                  onComplete={handleUploadComplete}
                  buttonClassName="w-full"
                >
                  <div className="flex items-center justify-center space-x-2 p-6 border-2 border-dashed border-border rounded-lg hover:border-primary/50 transition-colors">
                    <Upload className="w-6 h-6 text-muted-foreground" />
                    <div className="text-center">
                      <p className="text-sm font-medium text-foreground">
                        {uploadedReceipts.length > 0 ? "Add another receipt" : "Click to upload receipt"}
                      </p>
                      <p className="text-xs text-muted-foreground">PNG, JPG, PDF up to 10MB</p>
                    </div>
                  </div>
                </ObjectUploader>
              </div>
            </div>

            <FormField
              control={form.control}
              name="notes"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Notes (Optional)</FormLabel>
                  <FormControl>
                    <Textarea
                      rows={3}
                      placeholder="Add any additional notes about this expense"
                      {...field}
                      value={field.value || ""}
                      data-testid="textarea-notes"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex items-center justify-end space-x-3 pt-6">
              <Button
                type="button"
                variant="outline"
                onClick={() => {
                  form.reset();
                  setUploadedReceiptUrl(null);
                  setUploadedReceipts([]);
                  onCancel?.();
                }}
                data-testid="button-cancel"
              >
                Cancel
              </Button>
              <Button
                type="submit"
                disabled={createExpenseMutation.isPending || categoriesLoading}
                data-testid="button-submit"
              >
                {createExpenseMutation.isPending && (
                  <Loader2 className="w-4 h-4 mr-2 animate-spin" />
                )}
                {isEditMode ? "Update Expense" : "Add Expense"}
              </Button>
            </div>
          </form>
        </Form>
      </CardContent>
    </Card>
  );
}

