import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Calendar, ChevronLeft, ChevronRight } from "lucide-react";
import {
  Popover,
  PopoverContent,
  PopoverTrigger,
} from "@/components/ui/popover";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { MONTHS } from "@/lib/constants";

interface MonthYearSelectorProps {
  selectedMonth: number;
  selectedYear: number;
  onMonthYearChange: (month: number, year: number) => void;
}

export default function MonthYearSelector({
  selectedMonth,
  selectedYear,
  onMonthYearChange,
}: MonthYearSelectorProps) {
  const [isOpen, setIsOpen] = useState(false);
  const currentDate = new Date();
  const currentMonth = currentDate.getMonth() + 1;
  const currentYear = currentDate.getFullYear();

  const goToPreviousMonth = () => {
    if (selectedMonth === 1) {
      onMonthYearChange(12, selectedYear - 1);
    } else {
      onMonthYearChange(selectedMonth - 1, selectedYear);
    }
  };

  const goToNextMonth = () => {
    if (selectedMonth === 12) {
      onMonthYearChange(1, selectedYear + 1);
    } else {
      onMonthYearChange(selectedMonth + 1, selectedYear);
    }
  };

  const goToCurrentMonth = () => {
    onMonthYearChange(currentMonth, currentYear);
  };

  const handleMonthChange = (month: string) => {
    onMonthYearChange(parseInt(month), selectedYear);
  };

  const handleYearChange = (year: string) => {
    onMonthYearChange(selectedMonth, parseInt(year));
  };

  // Generate year options (current year Â± 5 years)
  const years = [];
  for (let year = currentYear - 5; year <= currentYear + 1; year++) {
    years.push(year);
  }

  const isCurrentMonth = selectedMonth === currentMonth && selectedYear === currentYear;

  return (
    <div className="flex items-center space-x-2">
